mapper.sql:
FROM
	qnarequests a
	join usermember b on b.umSeq = a.userMember_umSeq
	left join qnaanswers c on a.qrSeq = c.qnaRequests_qrSeq
where 1=1
	AND a.qrDelNy = #{shDelNy}
	AND a.qrStatus = #{shQrStatus}
	AND a.qrType = #{shQnaType}
	AND b.umseq Like CONCAT('%',#{shStaff},'%')



				AND(
					a.qrSeq Like CONCAT('%',#{shValue},'%')
					OR a.qrTitle Like CONCAT('%',#{shValue},'%')
					OR b.umName Like CONCAT('%',#{shValue},'%')

				)

			AND a.qrSeq = #{shValue}
			AND a.qrTitle Like CONCAT('%',#{shValue},'%')
			AND b.umName Like CONCAT('%',#{shValue},'%')



		 AND a.cdRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
		 AND a.cdModDate BETWEEN #{shDateStart} AND #{shDateEnd}

FROM
	qnaanswers a
	join staffmember b on b.sfSeq = a.staffMember_sfSeq
	join qnarequests c on c.qrSeq = a.qnaRequests_qrSeq
where 1=1
	AND a.qaDelNy = #{shDelNy}
	AND c.qrStatus = #{shQrStatus}
	AND b.sfseq Like CONCAT('%',#{shStaff},'%')



				AND(
					a.qaSeq Like CONCAT('%',#{shValue},'%')
					OR c.qrSeq Like CONCAT('%',#{shValue},'%')
					OR c.qrTitle Like CONCAT('%',#{shValue},'%')

				)

			AND c.qrSeq = #{shValue}
			AND c.qrTitle Like CONCAT('%',#{shValue},'%')
			AND a.qaSeq = #{shValue}




			 AND a.qaRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
			 AND a.qaModDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
        INSERT INTO qnaanswers (
			qaContent
			,staffMember_sfSeq
			,qnaRequests_qrSeq
			,qaDelNy
			,qaRegDate
			,qaModDate

        ) VALUES (
	        #{qaContent}
	        ,#{staffMember_sfSeq}
	        ,#{qnaRequests_qrSeq}
	        ,0
	        ,now()
	        ,now()
        )

            SELECT last_insert_id()

