mapper.insert:
INSERT INTO qnatable ( qrTitle ,qrContent ,qrType ,qrStatus ,userMember_umSeq ,staffMember_sfSeq ,qrDelNy ,qrRegDate ,qaDelNy ,qaModDate ,qaRegDate
) VALUES ( #{qrTitle} ,#{qrContent} ,#{qrType} ,0 ,#{userMember_umSeq} ,#{staffMember_sfSeq} ,0 ,now() ,0 ,now() ,now() )
SELECT last_insert_id()

